generated.css:
*,
*:before,
*:after {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

html {
  box-sizing: inherit;
}

body {
  font-family: "Gill Sans", "Gill Sans MT", Calibri, "Trebuchet MS", sans-serif;
  background-color: #fcfcfc;
}

ul > li {
  list-style-type: none;
}

.hide {
  display: none;
}

.visibilityHide {
  visibility: hidden;
}

section.ui-container {
  display: grid;
  grid-template-columns: 25em 45em;
  width: 70%;
  grid-gap: 20px;
  padding: 1rem 2rem;
  height: 80vh;
  min-height: 38rem;
  justify-content: center;
}
@media screen and (max-width: 1200px) {
  section.ui-container {
    grid-template-columns: 100%;
    width: 100%;
  }
}

.ui-price-card {
  border: 1px solid black;
  border-radius: 0.4em;
  padding: 0.4em;
  display: flex;
  -webkit-box-flex: flex;
  justify-content: space-between;
  align-items: center;
  background-color: #0b0b0b;
  line-height: 2em;
  font-family: "Kumbh Sans", sans-serif;
  margin: 0.6em 0.1em;
}
.ui-price-card:last-child {
  margin-bottom: 3em;
}
.ui-price-card__price-box {
  display: flex;
  flex-direction: column;
  background-color: #222222;
  color: whitesmoke;
  width: 25%;
  border-radius: 0.4em;
  padding: 0 0.2em;
}
.ui-price-card__price-box > span {
  width: 100%;
}
.ui-price-card__price-box > span:nth-child(1) {
  font-style: italic;
  font-size: 0.9em;
}
.ui-price-card__price-box > span:nth-child(2) {
  text-align: right;
  font-size: 1.5rem;
  font-weight: 600;
}
.ui-price-card__length-box {
  color: lightgray;
  font-style: italic;
}
.ui-price-card__length-box > div > span {
  font-weight: bold;
  font-style: normal;
  color: whitesmoke;
}

ul.ui-input-nav-lists {
  display: flex;
  -webkit-box-flex: flex;
  justify-content: space-around;
  align-items: center;
}
ul.ui-input-nav-lists > li {
  font-size: 1.4rem;
}

.ui-input {
  border-radius: 1.2em;
  transition: all 3ms linear;
  box-shadow: 0px 6px 20px -2px rgba(0, 0, 0, 0.27);
  background: linear-gradient(180deg, rgba(255, 255, 255, 0.45) 0%, rgba(255, 255, 255, 0.3735) 100%);
}
.ui-input-container {
  background-color: #0b0b0b;
  height: 50%;
  padding: 0.4em 0.3em;
  border-top-left-radius: 0.8em;
  border-top-right-radius: 0.8em;
}
.ui-input-nav {
  line-height: 2.6em;
}
.ui-input-nav-list {
  border-right: 1px solid #0b0b0b;
  width: 100%;
  text-align: center;
  cursor: pointer;
  color: #3a474b;
  background-color: #f7f7f7;
}
.ui-input-nav-list:nth-child(1) {
  border-top-left-radius: 0.4em;
}
.ui-input-nav-list:nth-child(3) {
  border-top-right-radius: 0.4em;
}
.ui-input-form-container {
  padding: 1em 0;
  text-align: center;
  background-color: #222222;
  border-bottom-left-radius: 0.8em;
  border-bottom-right-radius: 0.8em;
}
.ui-input-form-heading {
  margin-bottom: 1em;
  color: #444;
}
.ui-input-form.text {
  text-align: center;
  padding: 1em;
}
.ui-input-form.text > input {
  width: 100%;
  height: 10rem;
  border-radius: 0.8em;
  border-top-style: hidden;
  border-right-style: hidden;
  border-left-style: hidden;
  border-bottom-style: hidden;
  box-shadow: inset 0 0 0 2px #d6d6d6;
  outline: none;
  padding: 2em;
  background-color: #eeeeee;
  font-size: 1.5rem;
  color: #444;
  font-weight: 500;
  text-align: center;
}
.ui-input-form.text > input:focus {
  -webkit-box-shadow: 0px 2px 6px 3px #d7caccbe;
  box-shadow: 0px 2px 6px 3px #d7caccbe;
  border: 1px solid rgba(128, 128, 128, 0.575);
}
.ui-input-form.color, .ui-input-form.fontFamily {
  display: flex;
  -webkit-box-flex: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
}

.ui-input-color-lists,
.ui-input-fontFamily-lists {
  width: 90%;
  text-align: center;
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(5em, 1fr));
  grid-gap: 10px;
  justify-content: center;
}

.nav-active {
  background-color: #222222;
  color: white;
  cursor: auto;
}

.ui-input-form.text,
.ui-input-form.fontFamily,
.ui-input-form.color {
  display: none;
}

.ui-active {
  display: flex !important;
}

.ui-input-fontFamily-lists {
  width: 90%;
  text-align: center;
  display: grid;
  grid-template-columns: auto auto;
  grid-gap: 10px;
}

.ui-input-fontFamily-list {
  border: 1px solid silver;
  width: 10em;
  padding: 0.4em;
  border-radius: 0.2em;
  cursor: pointer;
  transition: background-color 0.3s ease-in-out;
}
.ui-input-fontFamily-list:hover {
  background-color: #3c7a89;
}
.ui-input-fontFamily-list:hover > image.ui-input-fontFamily-list__imageWhite {
  display: flex !important;
}
.ui-input-fontFamily-list__image {
  height: 22px;
}
.ui-input-fontFamily-list__imageWhite {
  height: 22px;
}

.btn-active {
  background-color: #3c7a89;
  display: flex;
  -webkit-box-flex: flex;
  justify-content: center;
  align-items: center;
}
.btn-active > img.ui-input-fontFamily-list__image {
  display: none !important;
}
.btn-active > img.ui-input-fontFamily-list__imageWhite {
  display: flex !important;
}

.input-color-list {
  border-radius: 0.2em;
  width: 5em;
  padding: 1em;
  text-align: center;
  color: #3f3e3e;
  cursor: pointer;
}
.input-color-list-heading {
  font-size: 0.8rem;
  margin-top: 0.4em;
}

.fa.fa-lightbulb-o {
  font-size: 1.8rem;
  transition: text-shadow, color 0.3s ease-in-out;
}

