body.background {
  height: 100vh;
  background: linear-gradient(180deg, #01101f 0%, #1b1b1b 68.37%, #a97171 100%);
  background-color: #2b1322;
  overflow: hidden;
}

.home-logo > img {
  position: relative;
  top: 2em;
  left: calc(1vw + 1rem);
  height: calc(1.6rem + 1vw);
}

.home-content__container {
  color: white;
  display: flex;
  -webkit-box-flex: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
}

.home-content {
  text-align: right;
  display: flex;
  -webkit-box-flex: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  align-items: end;
  position: relative;
  bottom: -7em;
  line-height: 4.8vw;
}
.home-content-heading {
  font-size: max(3.5vw, 1.6rem);
  font-family: "Kodchasan", sans-serif;
  font-style: italic;
  color: #edbacf;
}
.home-content-subheading {
  font-size: max(2.5vw, 1.2rem);
  font-family: "Kodchasan", sans-serif;
  width: 100%;
}
.home-content-btn {
  border: none;
  background: none;
  margin-top: 3em;
}
.home-content-btn > a {
  text-align: center;
  display: inline-block;
  width: 8em;
  height: 2.3em;
  font-family: "Caveat", cursive;
  font-size: 1.4rem;
  color: #dfdfdf;
  text-decoration: none;
  background: linear-gradient(180deg, #3e021f 0%, rgba(255, 25, 141, 0.73) 100%);
  padding: 0.5em 1.5em;
  border-radius: 0.2em;
  overflow: hidden;
}
.home-content-btn > a:after {
  display: inline-block;
  width: 7em;
  height: 2em;
  content: "";
  background: #ff93c7cf;
  position: relative;
  right: -3.5em;
  transform: rotate(100deg);
  opacity: 0.2;
  animation: reflect 4s linear 6s infinite forwards;
}
.home-content-btn > a:hover {
  background: linear-gradient(180deg, #e10871 0%, rgba(225, 9, 118, 0.73) 100%);
}

.home-content__shapes {
  width: 100%;
  display: flex;
  -webkit-box-flex: flex;
  justify-content: space-between;
  align-items: center;
}
.home-content__shapes-displayTab {
  width: 20em;
  min-width: 10em;
  height: 30em;
  border: 19px solid #363636;
  border-radius: 0.5em;
  background: linear-gradient(290.35deg, rgba(36, 20, 44, 0.45) 7.51%, rgba(8, 50, 88, 0.45) 89.23%);
  background-color: rgba(0, 0, 0, 0);
  background-color: #1b1b25;
  transform: rotate(30deg);
  position: relative;
  left: -90px;
  bottom: -10em;
}
.home-content__shapes-ball {
  width: 18em;
  height: 18em;
  display: flex;
  -webkit-box-flex: flex;
  justify-content: center;
  align-items: center;
}
.home-content__shapes-ball-object {
  width: 330px;
  height: 186px;
  background: linear-gradient(180deg, rgba(3, 7, 39, 0.3) 22.92%, rgba(49, 25, 25, 0.039) 100%);
  background-color: rgba(3, 7, 34, 0.144);
  border-radius: 50%;
}
.home-content__shapes-ball-shadow {
  width: 275px;
  height: 196px;
  background: linear-gradient(180deg, rgba(3, 7, 39, 0.3) 22.92%, rgba(49, 25, 25, 0.039) 100%);
  border-radius: 25em;
  position: relative;
  left: 181px;
  transform: skewX(-75deg);
  top: -5px;
}

@keyframes reflect {
  0% {
    right: -3.5em;
  }
  20% {
    right: 7em;
  }
  30% {
    right: 7em;
  }
  40% {
    right: 7em;
  }
  50% {
    right: 7em;
  }
  60% {
    right: 7em;
  }
  90% {
    right: 7em;
  }
  100% {
    right: 7em;
  }
}
@keyframes reflectionForSmallDevice {
  0% {
    right: -90%;
  }
  20% {
    right: 100%;
  }
  30% {
    right: 100%;
  }
  40% {
    right: 100%;
  }
  50% {
    right: 100%;
  }
  60% {
    right: 100%;
  }
  90% {
    right: 100%;
  }
  100% {
    right: 100%;
  }
}
@media screen and (max-width: 800px) {
  .home-content {
    line-height: 2.5rem;
  }
  .home-content__btn {
    margin-top: 1em;
  }

  .home-content__shapes-displayTab {
    border-width: 0.6em;
  }
  .home-content__shapes-ball {
    position: relative;
    right: -25%;
    width: 14em;
    height: 14em;
  }
  .home-content__shapes-ball-object {
    width: 250px;
    height: 120px;
    position: relative;
    bottom: -2em;
  }
  .home-content__shapes-ball-shadow {
    visibility: hidden;
  }
}
@media screen and (max-width: 670px) {
  .home-content {
    margin: 0 2em;
    text-align: left;
  }
}
@media screen and (max-width: 500px) {
  img.home-logo__logo {
    height: 7vw;
    top: 4vh;
    left: 3%;
  }

  .home-content {
    bottom: -6em;
  }
  .home-content-btn {
    width: 100%;
  }
  .home-content-heading {
    font-size: 1.4rem;
  }
  .home-content-subheading {
    font-size: 1.1rem;
  }

  a.home-content-btn__link-ui {
    width: 96vw;
    height: 1em;
    height: 2em;
    font-size: 1.2rem;
    padding: 0.4em 1.5em;
  }
  a.home-content-btn__link-ui:after {
    display: flex;
    right: -100%;
    animation: reflectionForSmallDevice 4s linear 8s infinite forwards !important;
  }
}
.ui-html {
  overflow-x: hidden;
}

.ui-body {
  min-height: 110vh;
  background: linear-gradient(180deg, #01101f 0%, #1b1b1b 68.37%, #a97171 100%);
  background-color: #2b1322;
  overflow-x: hidden;
}

.ui-logo {
  width: 100%;
  height: 15%;
  margin-left: 1em;
  margin-top: 1em;
  margin-bottom: 2em;
}
.ui-logo__logo {
  height: 2rem;
}

main.container {
  border: 1px solid white;
  width: 100%;
  display: flex;
  -webkit-box-flex: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
}

