sources.src\\sass\\_ui-container.scss:
section.ui-container {
	display: grid;
	grid-template-columns: 25em 45em;
	width: 70%;
	grid-gap: 20px;
	padding: 1rem 2rem;
	height: 80vh;
	min-height: 38rem;
	justify-content: center;

	@media screen and (max-width: 1200px) {
		grid-template-columns: 100%;
		width: 100%;
	}
}

.ui-price-card {
	border: 1px solid black;
	border-radius: 0.4em;
	padding: 0.4em;
	@include flexSpaceBetween;
	background-color: $uiPrimary;
	line-height: 2em;
	font-family: "Kumbh Sans", sans-serif;
	margin: 0.6em 0.1em;
	&:last-child {
		margin-bottom: 3em;
	}
	&__price-box {
		display: flex;
		flex-direction: column;
		background-color: $uiSecondary;
		color: whitesmoke;
		width: 25%;
		border-radius: 0.4em;
		padding: 0 0.2em;
		& > span {
			// @include borderTest(white);
			width: 100%;

			&:nth-child(1) {
				font-style: italic;
				font-size: 0.9em;
			}

			&:nth-child(2) {
				text-align: right;
				font-size: 1.5rem;
				font-weight: 600;
			}
		}
	}

	&__length-box {
		color: rgb(211, 211, 211);
		font-style: italic;
		& > div > span {
			font-weight: bold;
			font-style: normal;
			color: whitesmoke;
		}
	}
}

sources.src\\sass\\_helper.scss:
@mixin flexCentered {
	display: flex;
	-webkit-box-flex: flex;
	justify-content: center;
	align-items: center;
}

@mixin flexSpaceBetween {
	display: flex;
	-webkit-box-flex: flex;
	justify-content: space-between;
	align-items: center;
}

@mixin flexSpaceAround {
	display: flex;
	-webkit-box-flex: flex;
	justify-content: space-around;
	align-items: center;
}

@mixin flex($jc: null, $ai: null) {
	display: flex;
	-webkit-box-flex: flex;
	justify-content: $jc;
	align-items: $ai;
}

@mixin borderTest($color) {
	border: 1px solid $color;
}

@mixin glowingLight($bgColor) {
	text-shadow: 0 0 4px white, 0 0 4px $bgColor, 0 0 8px $bgColor,
		0 0 12px $bgColor, 0 0 16px $bgColor, 0 0 18px $bgColor;
	color: rgb(248, 248, 248);
}

@mixin measurementBarBorder($left) {
	width: 20px;
	display: inline-block;
	height: 7px;
	border-bottom: 2px solid white;

	@if ($left == "left") {
		border-left: 2px solid white;
	} @else {
		border-right: 2px solid white;
	}
}

