$inputBoxShadow: #d7caccbe;

ul.ui-input-nav-lists {
	@include flexSpaceAround;

	> li {
		font-size: 1.4rem;
	}
}

.ui-input {
	// border: 1px solid silver;
	border-radius: 1.2em;
	transition: all 3ms linear;
	box-shadow: 0px 6px 20px -2px rgba(0, 0, 0, 0.27);
	background: linear-gradient(
		180deg,
		rgba(255, 255, 255, 0.45) 0%,
		rgba(255, 255, 255, 0.3735) 100%
	);

	&-container {
		background-color: $uiPrimary;
		height: 50%;
		padding: 0.4em 0.3em;
		border-top-left-radius: 0.8em;
		border-top-right-radius: 0.8em;
	}
	&-nav {
		line-height: 2.6em;
		&-list {
			border-right: 1px solid $uiPrimary;
			width: 100%;
			text-align: center;
			cursor: pointer;
			color: #3a474b;

			background-color: rgb(247, 247, 247);

			&:nth-child(1) {
				border-top-left-radius: 0.4em;
			}
			&:nth-child(3) {
				border-top-right-radius: 0.4em;
			}
		}
	}

	&-form {
		&-container {
			padding: 1em 0;
			text-align: center;
			background-color: $uiSecondary;
			border-bottom-left-radius: 0.8em;
			border-bottom-right-radius: 0.8em;
		}

		&-heading {
			margin-bottom: 1em;
			color: #444;
		}

		&.text {
			text-align: center;
			padding: 1em;

			> input {
				width: 100%;
				height: 10rem;
				border-radius: 0.8em;
				border-top-style: hidden;
				border-right-style: hidden;
				border-left-style: hidden;
				border-bottom-style: hidden;
				box-shadow: inset 0 0 0 2px rgb(214, 214, 214);
				outline: none;
				padding: 2em;
				background-color: rgb(238, 238, 238);
				font-size: 1.5rem;
				color: #444;
				font-weight: 500;
				text-align: center;

				&:focus {
					-webkit-box-shadow: 0px 2px 6px 3px $inputBoxShadow;
					box-shadow: 0px 2px 6px 3px $inputBoxShadow;
					border: 1px solid rgba(128, 128, 128, 0.575);
				}
			}
		}

		&.color,
		&.fontFamily {
			@include flexCentered;
			flex-direction: column;
		}
	}
}

//same rule for fontFamily and color form

.ui-input-color-lists,
.ui-input-fontFamily-lists {
	width: 90%;
	text-align: center;
	display: grid;
	// grid-template-columns: auto auto;
	grid-template-columns: repeat(auto-fill, minmax(5em, 1fr));
	grid-gap: 10px;
	justify-content: center;
}

// active class for ui-input nav
.nav-active {
	background-color: $uiSecondary;
	color: rgb(255, 255, 255);
	cursor: auto;
}

.ui-input-form.text,
.ui-input-form.fontFamily,
.ui-input-form.color {
	display: none;
}

.ui-active {
	display: flex !important;
}

sources.src\\sass\\ui-input\\_ui-input-fontFamil:
//root color for headings
$uiHeadings: #918a8a;

.ui-input-fontFamily-lists {
	width: 90%;
	text-align: center;
	display: grid;
	grid-template-columns: auto auto;
	grid-gap: 10px;
}

.ui-input-fontFamily-list {
	@include borderTest(silver);
	width: 10em;
	padding: 0.4em;
	border-radius: 0.2em;
	cursor: pointer;
	transition: background-color 0.3s ease-in-out;
	&:hover {
		background-color: $primaryColor;

		& > image.ui-input-fontFamily-list__imageWhite {
			display: flex !important;
		}
	}
	&__image {
		height: 22px;
	}

	&__imageWhite {
		height: 22px;
	}
}

.btn-active {
	background-color: $primaryColor;
	@include flexCentered;
	& > img.ui-input-fontFamily-list__image {
		display: none !important;
	}
	& > img.ui-input-fontFamily-list__imageWhite {
		display: flex !important;
	}
}

