article.ui-display {
	background-color: #202020;
	background: linear-gradient(
		180deg,
		rgb(10, 10, 14) 18%,
		rgba(0, 0, 0, 0.8183474073223039) 100%
	);
	padding: 1em;
	border-radius: 1.2em;
}

.ui-display-userText {
	&-container {
		@include borderTest(white);
		width: 100%;
		height: 20em;
		background-color: rgb(48, 47, 47);
		margin-top: 5em;
		@include flexCentered;
		flex-direction: column;
		@media screen and (max-width: 1200px) {
			margin-top: 2em;
		}
	}

	&-wrapper {
		@include flexSpaceBetween;
	}

	&-text {
		// @include borderTest(white);
		width: auto;
		height: auto;
		color: rgb(244, 244, 244);
		font-size: 3.4rem;
		letter-spacing: 2px;
		font-family: "Tangerine", cursive;
		text-shadow: rgb(181, 181, 181) 0px 1px 0px, rgb(169, 169, 169) 0px 2px 0px,
			rgb(148, 148, 148) 0px 3px 0px, rgb(125, 125, 125) 0px 4px 0px,
			rgba(0, 0, 0, 0.23) 0px 0px 5px, rgba(0, 0, 0, 0.43) 0px 1px 3px,
			rgba(0, 0, 0, 0.4) 1px 4px 6px, rgba(0, 0, 0, 0.38) 0px 5px 10px,
			rgba(0, 0, 0, 0.25) 3px 7px 12px;
	}
}

//measurement bar

.measurementBar {
	&-width {
		width: 20px;
		display: inline-block;
		height: 7px;
		border-bottom: 2px solid white;
		border-left: 2px solid white;
		border-right: 2px solid white;
		&-length {
			padding: 4px 0;
		}
	}

	&-height {
		width: 4px;
		height: 46px;
		position: relative;
		right: -1em;
		// top: -5px;
		border-right: 2px solid white;
		border-top: 2px solid white;
		border-bottom: 2px solid white;

		&-wrapper {
			@include flexCentered;
			right: -1.3em;
			position: relative;
			// width: 6px;
		}

		&-length {
			color: white;
			position: relative;
			right: -1.4em;
		}
	}

	&-container-bottom {
		@include flexCentered;
		color: whitesmoke;
		flex-direction: column;
		position: relative;
		left: -20px;
	}
}

.neonOn {
	text-shadow: rgb(255, 255, 255) 0px 0px 5px, rgb(255, 255, 255) 0px 0px 10px,
		rgb(255, 144, 255) 0px 0px 20px, rgb(255, 144, 255) 0px 0px 30px,
		rgb(255, 144, 255) 0px 0px 40px, rgb(255, 144, 255) 0px 0px 55px,
		rgb(255, 144, 255) 0px 0px 75px;
}

//canvas

canvas#displayText {
	@include borderTest(orange);
	display: none;
}

/* The switch - the box around the slider */
.switch {
	position: relative;
	display: inline-block;
	width: 65px;
	height: 30px;
}

/* Hide default HTML checkbox */
.switch input {
	opacity: 0;
	width: 0;
	height: 0;
}

/* The slider */
.slider {
	position: absolute;
	cursor: pointer;
	top: 0;
	left: 0;
	right: 0;
	bottom: 0;
	background-color: #ccc;
	-webkit-transition: 0.4s;
	transition: 0.4s;
	@include flexCentered;

	> span {
		color: rgb(92, 90, 90);
		font-weight: 600;
	}
}

.slider:before {
	position: absolute;
	content: "";
	height: 23px;
	width: 23px;
	left: 4px;
	bottom: 4px;
	background-color: white;
	-webkit-transition: 0.4s;
	transition: 0.4s;
}

input ~ span span:first-of-type {
	opacity: 0;
}

input:checked + .slider {
	background-color: #4397db;
}

input:focus + .slider {
	box-shadow: 0 0 1px #2196f3;
}

input:checked + .slider:before {
	-webkit-transform: translateX(31px);
	-ms-transform: translateX(31px);
	transform: translateX(31px);
}

input:checked ~ span span:last-of-type {
	opacity: 0;
}

input:checked ~ span span:first-of-type {
	opacity: 1;
	color: whitesmoke;
}
/* Rounded sliders */
.slider.round {
	border-radius: 34px;
}

.slider.round:before {
	border-radius: 50%;
}

