/* The switch - the box around the slider */
.switch {
	position: relative;
	display: inline-block;
	width: 65px;
	height: 30px;
}

/* Hide default HTML checkbox */
.switch input {
	opacity: 0;
	width: 0;
	height: 0;
}

/* The slider */
.slider {
	position: absolute;
	cursor: pointer;
	top: 0;
	left: 0;
	right: 0;
	bottom: 0;
	background-color: #ccc;
	-webkit-transition: 0.4s;
	transition: 0.4s;
	@include flexCentered;

	> span {
		color: rgb(92, 90, 90);
		font-weight: 600;
	}
}

.slider:before {
	position: absolute;
	content: "";
	height: 23px;
	width: 23px;
	left: 4px;
	bottom: 4px;
	background-color: white;
	-webkit-transition: 0.4s;
	transition: 0.4s;
}

input ~ span span:first-of-type {
	opacity: 0;
}

input:checked + .slider {
	background-color: #4397db;
}

input:focus + .slider {
	box-shadow: 0 0 1px #2196f3;
}

input:checked + .slider:before {
	-webkit-transform: translateX(31px);
	-ms-transform: translateX(31px);
	transform: translateX(31px);
}

input:checked ~ span span:last-of-type {
	opacity: 0;
}

input:checked ~ span span:first-of-type {
	opacity: 1;
	color: whitesmoke;
}
/* Rounded sliders */
.slider.round {
	border-radius: 34px;
}

.slider.round:before {
	border-radius: 50%;
}

