		margin-top: 0.4em;
	}

	//hover effect per bulb
	&.orange {
		&:hover {
			i[data-color="orange"] {
				@include glowingLight(orange);
			}
		}
	}

	//red
	&.lightRed {
		&:hover {
			i[data-color="light red"] {
				@include glowingLight($lightRed);
			}
		}
	}
	//red
	&.red {
		&:hover {
			i[data-color="red"] {
				@include glowingLight(red);
			}
		}
	}
	//deepBlue
	&.deepBlue {
		&:hover {
			i[data-color="deep blue"] {
				@include glowingLight($deepBlue);
			}
		}
	}
	//electric blue
	&.electricBlue {
		&:hover {
			i[data-color="electric blue"] {
				@include glowingLight($electricBlue);
			}
		}
	}
	//tropical blue
	&.tropicalBlue {
		&:hover {
			i[data-color="tropical blue"] {
				@include glowingLight($tropicalBlue);
			}
		}
	}
	//ice blue
	&.iceBlue {
		&:hover {
			i[data-color="ice blue"] {
				@include glowingLight($iceBlue);
			}
		}
	}
	//green
	&.green {
		&:hover {
			i[data-color="green"] {
				@include glowingLight($green);
			}
		}
	}

	//mint green
	&.mintGreen {
		&:hover {
			i[data-color="mint green"] {
				@include glowingLight($mintGreen);
			}
		}
	}

	//deep green
	&.deepGreen {
		&:hover {
			i[data-color="deep green"] {
				@include glowingLight($deepGreen);
			}
		}
	}
	//warm white
	&.warmWhite {
		transition: background-color 0.3s ease-in-out;
		&:hover {
			i[data-color="warm white"] {
				@include glowingLight($warmWhite);
			}
			background-color: rgba(97, 97, 97, 0.582);
			color: white;
		}
	}
	//white
	&.white {
		transition: background-color 0.3s ease-in-out;
		&:hover {
			i[data-color="white"] {
				@include glowingLight($white);
			}
			background-color: rgba(97, 97, 97, 0.582);
			color: white;
		}
	}
}

.fa.fa-lightbulb-o {
	font-size: 1.8rem;
	transition: text-shadow, color 0.3s ease-in-out;
}

